import { useState } from 'react';
import Button from '../Button';
import SuspendedList from '../SuspendedList';
import TextBox from '../TextBox';
import './Form.css'


function Form(props) {

    const [name,setNome] = useState('')
    const [cargo,setCargo] = useState('')
    const [imagem,setImagem] = useState('')
    const [time,setTime] = useState('')

    const whenSaving = (event) => {
        event.preventDefault()
        props.whenRegistered({
            name,
            cargo,
            imagem,
            time
        })
    }

    return (
         <section className="formBox">
            <form onSubmit={whenSaving}>
                <h2>Preencha os dados para criar o card do colaborador</h2>
                <TextBox 
                    mandatory={true}
                    label="Nome"
                    placeholder="Digite seu nome"
                    stateValue={name}
                    whenChanged = {stateValue => setNome(stateValue)}
                    />
                <TextBox 
                    mandatory={true} 
                    label="Cargo" 
                    placeholder="Digite seu cargo"
                    stateValue={cargo}
                    whenChanged = {stateValue => setCargo(stateValue)}
                    />
                <TextBox 
                    label="Imagem" 
                    placeholder="Digite o endereÃ§o da imagem"
                    stateValue={imagem}
                    whenChanged = {stateValue => setImagem(stateValue)}
                />
                <SuspendedList 
                    mandatory={true} 
                    label="Time" 
                    itens={props.teams}
                    stateValue={time}
                    whenChanged = {stateValue => setTime(stateValue)}
                    />
                <Button> 
                    Criar Card 
                </Button>
            </form>
         </section>
    )

}

export default Form
