import { useState } from 'react';
import Banner from './components/Banner';
import Form from './components/Form';
import Team from './components/Team';


function App() {

  const teams = [
    {
      nome: '',
      primaryColor: '',
      secondaryColor: ''
    }
  ]

  const [users, setUsers] = useState([])

  const whenUserAdded = (user) => {
    console.log(user)
    setUsers([...users, user])
  }

  return (
    <div className="App">
      <Banner/>
      <Form whenRegistered={user => whenUserAdded(user)} />
      <Team name="Programação"/>
      <Team name="Frontend"/>
      <Team name="Data Science"/>
    </div>
  );
}

export default App;
