import { useState } from 'react';
import Button from '../Button';
import SuspendedList from '../SuspendedList';
import TextBox from '../TextBox';
import './Form.css'


function Form() {

    const teams = [
        'Programação',
        'Front-End',
        'Data Science',
        'Devops',
        'UX e Design',
        'Mobile',
        'Inovação e Gestão'
]

    const [nome,setNome] = useState('')
    const [cargo,setCargo] = useState('')
    const [imagem,setImagem] = useState('')

    const whenSaving = (event) => {
        event.preventDefault()
    }

    return (
         <section className="formBox">
            <form onSubmit={whenSaving}>
                <h2>Preencha os dados para criar o card do colaborador</h2>
                <TextBox 
                    mandatory={true}
                    label="Nome"
                    placeholder="Digite seu nome"
                    value={nome}
                    />
                <TextBox mandatory={true} label="Cargo" placeholder="Digite seu cargo"/>
                <TextBox label="Imagem" placeholder="Digite o endereço da imagem"/>
                <SuspendedList mandatory={true} label="Time" itens={teams}/>
                <Button> Criar Card </Button>
            </form>
         </section>
    )

}

export default Form
